require 'sinatra'

class FishApp < Sinatra::Base
  def initialize

    super

    @fish = [{:name => 'koifish',
              :description => 'Koifish are beautiful, friendly fresh water fish commonly found in ponds.',
              :img => 'http://4.bp.blogspot.com/-6n7llgNNFgA/Uh4q06pfMpI/AAAAAAAAIBY/qNaQYcFoWvY/s1600/most+beautiful+koi+fish+images+of+Japanese+(8).jpg',
              :wikipedia => 'http://en.wikipedia.org/wiki/Koifish'},
             {:name => 'starfish',
              :description => 'Description: Starfish are violent, angry fish that rule the sea. They normally are found fighting in salt water.',
              :img => 'data:image/jpeg;base64,',
              :wikipedia => 'http://en.wikipedia.org/wiki/Starfish'},
             {:name => 'shark',
              :description => 'Description: Sharks live in salt water and most of them are harmless.',
              :img => 'http://upload.wikimedia.org/wikipedia/commons/thumb/8/8a/Surfacing_great_white_shark.jpg/800px-Surfacing_great_white_shark.jpg',
              :wikipedia => 'http://en.wikipedia.org/wiki/Shark'}]
    @index = 0

  end

  #iterate through @fish to find the fish name and index
  #create a new link

  get '/' do
    erb :homepage, :locals => {:fish_hash => @fish} # get me the dogs view file
  end

  get '/:id' do
    @index = params[:id].to_i

    erb :fish
  end

end
